def test_tokenization(sample_series: list[float]):
    context_length = 256
    low_limit = -5.0
    high_limit = 5.0
    num_bins = 1024
    pad_token_id = num_bins

    data_list = [{'target': [sample_series]}]

    pointers = make_window_pointers(data_list, context_length)
    assert len(pointers) == len(sample_series) - context_length

    ds = ChronosDataset(
        data_list,
        pointers,
        context_length,
        horizon=1,
        low_limit=low_limit,
        high_limit=high_limit,
        num_bins=num_bins,
        pad_token_id=pad_token_id
    )

    x_tensor, y_tensor = ds[0]
    assert isinstance(x_tensor, torch.Tensor)
    assert isinstance(y_tensor, torch.Tensor)

    x_np = x_tensor.numpy()
    y_np = y_tensor.numpy()

    x_tokens, y_tokens, used_scale = tokenized_window_from_series(
        sample_series,
        start_idx=0,
        context_length=context_length,
        low_limit=low_limit,
        high_limit=high_limit,
        num_bins=num_bins,
        pad_token_id=pad_token_id
    )

    np.testing.assert_array_equal(x_np, x_tokens)
    np.testing.assert_array_equal(y_np, y_tokens)

    first_nan_index = np.argwhere(np.isnan(sample_series)).flatten()[0]
    assert x_tokens[first_nan_index] == pad_token_id

    assert np.all((x_tokens >= 0) & (x_tokens < num_bins) | (x_tokens == pad_token_id))
    assert np.all((y_tokens >= 0) & (y_tokens < num_bins) | (y_tokens == pad_token_id))

    assert np.isfinite(used_scale)
